package week1.day2;

public class CheckIfNumberIsPostiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		 int number=35;
		 if (number<=-1) {
		 	System.out.println("The Number is Negative  :" +number);
		 }
		 else if (number>=1) {
		 	System.out.println("The Number is Postive  :" +number);
		 }
		 else {
		 	System.out.println("The Number is Neither Postive nor Negative  :"+number);
		 }

	}

}
