package week1.day2;

public class ConvertANegativeNumberToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int number=-40;
if (number<0) {
	int converted=number*-1;
	System.out.println("Number was Converted from Negative to Positive :  "  +converted);
}else {
	System.out.println("Number was Positive : "+number);
}
	}

}
